#!/bin/bash

sudo dnf update && sudo dnf install https://download1.rpmfusion.org/free/fedora/rpmfusion-free-release-$(rpm -E %fedora).noarch.rpm && sudo dnf install https://download1.rpmfusion.org/nonfree/fedora/rpmfusion-nonfree-release-$(rpm -E %fedora).noarch.rpm && sudo dnf group update core && sudo dnf install java-latest-openjdk.x86_64 && sudo dnf install java-latest-openjdk-devel.x86_64 && sudo dnf install gstreamer1-plugins-{bad-\*,good-\*,base} gstreamer1-plugin-openh264 gstreamer1-libav --exclude=gstreamer1-plugins-bad-free-devel && sudo dnf install lame\* --exclude=lame-devel && sudo dnf group upgrade --with-optional Multimedia && sudo dnf install fedora-workstation-repositories && sudo dnf config-manager --set-enabled google-chrome && sudo dnf install google-chrome-stable && sudo dnf install snapd && sudo ln -s /var/lib/snapd/snap /snap && sudo dnf install stacer ckb-next akmod-nvidia
